import React from "react";

import { BsArrowUpRight } from "react-icons/bs";

import Image1 from "../assets/html.png";
import Image2 from "../assets/css.png";
import Image3 from "../assets/javascript.png";
import Image4 from "../assets/bootstrap.png";
import Image5 from "../assets/tailwindcss.png";
import Image6 from "../assets/php.png";
import Image7 from "../assets/sharp.png";

import { motion } from "framer-motion";
import { fadeIn } from "../variants";

const services = [
  {
    name: "Programming Languages",
    description: "HTML, CSS, JavaScript, PHP",
    images: [
      { src: Image1, alt: "HTML" },
      { src: Image2, alt: "CSS" },
      { src: Image3, alt: "JavaScript" },
      { src: Image6, alt: "PHP" },
      { src: Image7, alt: "C#" },
    ],
    link: "learn more",
  },
  {
    name: "Framework",
    description: "TailwindCss, Bootstrap",
    images: [
      { src: Image5, alt: "TailwindCss" },
      { src: Image4, alt: "Bootstrap" },
    ],
    link: "learn more",
  },
];

const Services = () => {
  return (
    <section className="section" id="services">
      <div className="container mx-auto">
        <div className="flex flex-col lg:flex-row">
          <motion.div
            variants={fadeIn("right", 0.3)}
            initial="hidden"
            whileInView={"show"}
            viewport={{ once: false, amount: 0.3 }}
            className="flex-1 lg:bg-bottom bg-no-repeat mix-blend-screen mb-12 lg:mb-0"
          >
            <h2 className="h2 text-accent mb-6">What I Do.</h2>
            <h3 className="h3 max-w-[455px] mb-16">
              Some programming languages & tools that I can
            </h3>
            <a href="https://github.com/KukuhAgung" className="btn btn-sm py-5">
              Go to my github
            </a>
          </motion.div>

          <motion.div
            variants={fadeIn("left", 0.5)}
            initial="hidden"
            whileInView={"show"}
            viewport={{ once: false, amount: 0.3 }}
            className="flex-1"
          >
            <div>
              {services.map((service, index) => {
                const { name, description } = service;
                return (
                  <div
                    className="border-white/20 h-[146px] mb-[38px] flex"
                    key={index}
                  >
                    <div className="max-w-[476px]">
                      <h4 className="text-[20px] tracking-wider font-primary font-semibold mb-6">
                        {name}
                      </h4>
                      <p className="font-secondary leading-tight">
                        {description}
                      </p>
                      <div className="flex flex-row w-[40px] gap-7 max-w-full mt-5">
                        {service.images.map((image, idx) => (
                          <motion.img
                            key={idx}
                            src={image.src}
                            alt={image.alt}
                            variants={fadeIn}
                            initial="hidden"
                            animate="visible"
                          />
                        ))}
                      </div>
                    </div>
                    {/* <div className="flex flex-col flex-1 items-end">
                      <a
                        href="#"
                        className="btn w-9 h-9 mb-[42px] flex justify-center items-center"
                      >
                        <BsArrowUpRight />
                      </a>
                    </div> */}
                  </div>
                );
              })}
            </div>
          </motion.div>
        </div>
      </div>
    </section>
  );
};

export default Services;
